function mealdates_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('mealdates');
	if(!$arrPerm[1]){
		return false;
	}

	$data['MealID'] = makeSafe($_REQUEST['MealID']);
		if($data['MealID'] == empty_lookup_value){ $data['MealID'] = ''; }
	$data['MealDate'] = intval($_REQUEST['MealDateYear']) . '-' . intval($_REQUEST['MealDateMonth']) . '-' . intval($_REQUEST['MealDateDay']);
	$data['MealDate'] = parseMySQLDate($data['MealDate'], '<%%creationDate%%>');
	$data['MealTime'] = makeSafe($_REQUEST['MealTime']);
		if($data['MealTime'] == empty_lookup_value){ $data['MealTime'] = ''; }
	if($data['MealID']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Meal': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	if($data['MealDate']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Date': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	// hook: mealdates_before_insert
	if(function_exists('mealdates_before_insert')){
		$args=array();
		if(!mealdates_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('insert into `mealdates` set       `MealID`=' . (($data['MealID'] !== '' && $data['MealID'] !== NULL) ? "'{$data['MealID']}'" : 'NULL') . ', `MealDate`=' . (($data['MealDate'] !== '' && $data['MealDate'] !== NULL) ? "'{$data['MealDate']}'" : 'NULL') . ', `MealTime`=' . (($data['MealTime'] !== '' && $data['MealTime'] !== NULL) ? "'{$data['MealTime']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"mealdates_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: mealdates_after_insert
	if(function_exists('mealdates_after_insert')){
		$res = sql("select * from `mealdates` where `MealDateID`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!mealdates_after_insert($data, getMemberInfo(), $args)){ return (get_magic_quotes_gpc() ? stripslashes($recID) : $recID); }
	}

	// mm: save ownership data
	sql("insert ignore into membership_userrecords set tableName='mealdates', pkValue='$recID', memberID='".getLoggedMemberID()."', dateAdded='".time()."', dateUpdated='".time()."', groupID='".getLoggedGroupID()."'", $eo);

	return (get_magic_quotes_gpc() ? stripslashes($recID) : $recID);
}

function mealdates_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
