	}

	// hook: mealdates_before_delete
	if(function_exists('mealdates_before_delete')){
		$args=array();
		if(!mealdates_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	sql("delete from `mealdates` where `MealDateID`='$selected_id'", $eo);

	// hook: mealdates_after_delete
	if(function_exists('mealdates_after_delete')){
		$args=array();
		mealdates_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='mealdates' and pkValue='$selected_id'", $eo);
}

