	$o=array('silentErrors' => true);
	sql('update `mealdates` set       `MealID`=' . (($data['MealID'] !== '' && $data['MealID'] !== NULL) ? "'{$data['MealID']}'" : 'NULL') . ', `MealDate`=' . (($data['MealDate'] !== '' && $data['MealDate'] !== NULL) ? "'{$data['MealDate']}'" : 'NULL') . ', `MealTime`=' . (($data['MealTime'] !== '' && $data['MealTime'] !== NULL) ? "'{$data['MealTime']}'" : 'NULL') . " where `MealDateID`='".makeSafe($selected_id)."'", $o);
