	$templateCode=str_replace('<%%UPLOADFILE(MealTime)%%>', '', $templateCode);

	// process values
	if($selected_id){
		$templateCode=str_replace('<%%VALUE(MealDateID)%%>', htmlspecialchars($row['MealDateID'], ENT_QUOTES, 'iso-8859-1'), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(MealDateID)%%>', urlencode($urow['MealDateID']), $templateCode);
		$templateCode=str_replace('<%%VALUE(MealID)%%>', htmlspecialchars($row['MealID'], ENT_QUOTES, 'iso-8859-1'), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(MealID)%%>', urlencode($urow['MealID']), $templateCode);
		$templateCode=str_replace('<%%VALUE(MealDate)%%>', @date('m/d/Y', @strtotime(htmlspecialchars($row['MealDate'], ENT_QUOTES, 'iso-8859-1'))), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(MealDate)%%>', urlencode(@date('m/d/Y', @strtotime(htmlspecialchars($urow['MealDate'], ENT_QUOTES, 'iso-8859-1')))), $templateCode);
		$templateCode=str_replace('<%%VALUE(MealTime)%%>', htmlspecialchars($row['MealTime'], ENT_QUOTES, 'iso-8859-1'), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(MealTime)%%>', urlencode($urow['MealTime']), $templateCode);
	}else{
		$templateCode=str_replace('<%%VALUE(MealDateID)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(MealDateID)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(MealID)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(MealID)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(MealDate)%%>', '<%%creationDate%%>', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(MealDate)%%>', urlencode('<%%creationDate%%>'), $templateCode);
		$templateCode=str_replace('<%%VALUE(MealTime)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(MealTime)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode=str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode=str_replace('<%%', '<!-- ', $templateCode);
	$templateCode=str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	// hook: mealdates_dv
	if(function_exists('mealdates_dv')){
		$args=array();
		mealdates_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
